import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import "./stateinfo.css";
import { stateInfoAction } from "../../store/actions/stateInfoActions";
import LiveStats from "./LiveStats";


const StateInfo = (props) => {
    const [impData, setImpData] = useState({})

    useEffect(() => {
        props.getStatesData()
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [])


    useEffect(() => {

        if (props.stateInfo.length !== 0) {
            const stateCode = props.match.params.statename
            const index = props.stateInfo.findIndex(name => name.statecode === stateCode)
            const state = props.stateInfo[index]
            setImpData(state)
        }
    }, [props])


    return (
        <div className="container mt-3">
            <div class="bc-icons-2 bread">

                <nav aria-label="breadcrumb ">
                    <ol class="breadcrumb grey lighten-4">
                        <li class="breadcrumb-item"><Link class="black-text" to="/">Home</Link></li>
                        <li class="breadcrumb-item"><span> {impData.state} </span></li>
                    </ol>
                </nav>
            </div>


            <div className="state-counts ">
            <div className="row">
                    <div className="col-2 stat-box rgba-red-slight">
                        <p>Cnfrm</p>
                        <p>{props.confirmed}</p>
                    </div>
                    <div className="col-2 stat-box rgba-blue-slight">
                        <p>Actv</p>
                        <p>{props.active}</p>

                    </div>
                    <div className="col-2 stat-box rgba-green-slight">
                        <p>Rcvrd</p>
                        <p>{props.recovered}</p>

                    </div>
                    <div className="col-2 stat-box rgba-grey-slight">
                        <p>Dcsd</p>
                        <p>{props.deceased}</p>

                    </div>
                </div>                
            </div>
        </div>
    )
}

const mapStateToProps = state => {
    return {
        stateInfo: state.stateInfo.statesInfoData
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        getStatesData: () => dispatch(stateInfoAction())
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(StateInfo)
