import React, { useState } from 'react';
import './boxmessage.css';
import { useEffectOnce } from 'react-use'

const BoxMessage = () => {
	const [messageBox, setMessageBox] = useState(true)

	useEffectOnce(() => {
		const handleMessageBox = () => {
			setMessageBox(false)
		}
	})

	return (
		<div>
			{messageBox &&
				<div className="card-container">
					<div className="card rgba-blue-slight card-styles">
						<div className="card-body">
							<i onClick={handleMessageBox} className="d-flex justify-content-end fas fa-times-circle cc_pointer"></i>
							<p className="card-text">
								Some quick example text to build on the panel title and make up the bulk of the panel's
								content. Some quick example text to build on the panel title and make up the bulk of the
								panel's content.
						</p>
						</div>
					</div>
				</div>
			}
		</div>
	);
};

export default BoxMessage;
