import { SET_MODE_DARK, SET_MODE_LIGHT } from '../actions/darkModeActions';

const initialState = {
    mode: false
}

const darkModeReducer = (state = initialState, action) => {
    if (action.type === SET_MODE_DARK) {
        return { mode: true }
    } if (action.type === SET_MODE_LIGHT) {
        return { mode: false }
    } else {
        return state
    }
}

export default darkModeReducer
