import React, { useState } from 'react';
import {connect} from 'react-redux';

let factArr = []

const Factoids = (props) => {
    const [factoid, setFactoid] = useState(0)

    props.factoidsData.forEach(element => {
        factArr.push(element.banner)
    });

    setInterval(() => {
        let currentId = factoid
        setFactoid(currentId + 1)
    }, 2000);    

    const main = factArr[factoid % factArr.length]

    return(
        <div className="d-flex justify-content-center mt-3">
            <p>{factoid}</p>
        </div>
    )
} 

const mapStateToProps = state => {
    return {
        factoidsData: state.stats.factoids
    }
}

export default connect(mapStateToProps)(Factoids)
