import { SET_MODE } from '../actions/darkModeActions';



const darkModeReducer = (state = {mode : false}, action) => {
    // console.log(action)
    if(action.type === SET_MODE) {
        return {mode : action.payload}   
    } return state
}

export default darkModeReducer
