import React from "react";
import { connect } from "react-redux";
import { useEffectOnce, useLocalStorage } from "react-use";
import axios from "axios";

const LatestUpdates = (props) => {
  //   console.log(props.getLatestUpdates.slice().reverse()[0].timestamp.toString());

  const [lastViewedLog, setLastViewedLog] = useLocalStorage(
    "lastViewedLog",
    null
  );
  const [newUpdate, setNewUpdate] = useLocalStorage("newUpdate", false);

  useEffectOnce(() => {
    const lastTimestamp = props.getLatestUpdates
      .slice()
    if (lastTimestamp !== lastViewedLog) {
      setNewUpdate(true);
      setLastViewedLog(lastTimestamp);
    }
  });

  useEffectOnce(() => {
    axios
      .get("https://api.covid19india.org/updatelog/log.json")
      .then((response) => {
        const lastTimestamp = response.data
          .slice()
          .reverse()[0]
          .timestamp.toString();
        if (lastTimestamp !== lastViewedLog) {
          setNewUpdate(true);
          setLastViewedLog(lastTimestamp);
        }
      })
      .catch((err) => {
        console.log(err);
      });
  });

  return <div>{/* bell icon to be added  */}</div>;
};

const mapStateToProps = (state) => {
  return {
    getLatestUpdates: state.stats.latestUpdates,
  };
};

export default connect(mapStateToProps)(LatestUpdates);
