import React, { useEffect } from "react";
import { connect } from "react-redux";
import { stateInfoAction } from "../../store/actions/stateInfoActions";


const StateInfo = (props) => {

    useEffect(() => {
        props.getStatesData()
    },[null])

    const stateCode = props.match.params.stateCode

    const index = props.statesData.findIndex(name => name.statecode === stateCode)
    console.log(index)
    const state = props.statesData[index]


    return (
        <div>
            state information inDEEEP thats wat she said
            <div>
                <p>
                    In the state OF fucking {state.state}
                </p>
            </div>
        </div>
    )
}

const mapStateToProps = state => {
    return {
        statesData: state.stats.statesData
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        getStatesData: () => dispatch(stateInfoAction())
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(StateInfo)
