import React, {useEffect, useState} from "react";
import './districtzone.css'

const DistrictZoneGraph = (props) => {
  const [redZone, setRedZone] = useState([])
  const [orangeZone, setOrangeZone] = useState([])
  const [greenZone, setGreenZone] = useState([])
  

const red = []
const orange = []
const green = []

useEffect(() => {

  props.zoneData.map(zone => {

  
    zone.zone === 'Red' && red.push(zone.district)
    zone.zone === 'Orange' && orange.push(zone.district)
    zone.zone === 'Green' && green.push(zone.district)

    setRedZone(red)

  })
},[])


  return (
    <div className="zones-data">
      <div className="row zone-col">
        <div className=" red lighten-4 zone">
          {red.map(dist => (
            <p>{dist}</p>
          ))}
        </div>
        <div className=" orange lighten-4 zone">
        {orange.map(dist => (
            <p>{dist}</p>
          ))}
        </div>
        <div className=" green lighten-4 zone">
        {green.map(dist => (
            <p>{dist}</p>
          ))}
        </div>
      </div>
    </div>
  )
}

export default DistrictZoneGraph
