import axios from "axios";

export const GET_LIVE_STATS = "GET_LIVE_STATS";
export const ERROR_LIVE_STATS = "ERROR_LIVE_STATS";
export const FETCHING_LIVE_STATS = "FETCHING_LIVE_STATS";
export const GET_STATES_DATA = "GET_STATES_DATA";
export const GET_TIME_SERIES = "GET_TIME_SERIES";
export const GET_TEST_SAMPLES = "GET_TEST_SAMPLES";


export const getLiveStatsAction = () => {
  return (dispatch) => {
    dispatch({ type: FETCHING_LIVE_STATS });
    const statsRequest = "https://api.covid19india.org/data.json"
    const factoidsRequest = "https://api.covid19india.org/data.json"
    axios
      .all([statsRequest, factoidsRequest])
      .then(axios.spread((...resp) => {
        const statsResp = resp[0]
        const factoidsRequest = resp[1]
        console.log(statsResp)
        // dispatch({type:GET_LIVE_STATS, payload:resp.data.statewise[0]})  
        dispatch({type:GET_STATES_DATA, payload:resp.data.statewise.slice(1, -1)})
        dispatch({type:GET_TIME_SERIES, payload:resp.data.cases_time_series})
        dispatch({type:GET_TEST_SAMPLES, payload:resp.data.tested})
      }))
      .catch((err) => {
        console.log(err);
        dispatch({type:ERROR_LIVE_STATS, payload:'Error. Try again'})
      });
  };
};
