import React, { useEffect } from 'react';
import './demographics.css'
import { connect } from 'react-redux';

const SelectOptions = (props) => {

useEffect(() => {
    props.allStates
})

    return (
        <div className="container">
            <div className="row select-option">
                <select
                    className=" btn btn-outline-primary dropdown-toggle"
                >
                    <option value="Select">--Select State--</option>
                    {props.allStates.map((state, index) => (
                        <option key={index} value="genderwise">{state.state}</option>
                    ))}

                </select>


                <select
                    className="  btn btn-outline-primary dropdown-toggle"
                >
                    <option value="Select">--Select--</option>
                    <option value="genderwise">Gender Wise Data</option>
                    <option value="currentstatus">Current Status</option>
                    <option value="transmissiontype">Type of Transmission</option>
                </select>
            </div>
        </div>
    )
}

const mapStateToProps = state => {
    return {
        allStates: state.stats.statesData
    }
}

export default connect(mapStateToProps)(SelectOptions)
